using namespace std; int main() { cin.tie(0); ios_base::sync_with_stdio(0); int block_size = 320; int block_num = 320; int n; cin >> n; vector<int> a(n + 1); vector<vector<int>> block(block_num); vector<vector<int>> cnt(block_num, vector<int>(n + 1)); for (int i = 1; i <= n; i++) { cin >> a[i]; int block_id = i / block_size; block[block_id].push_back(a[i]); cnt[block_id][a[i]]++; } int q; cin >> q; int last_ans = 0; auto decode = [&](int& x) { x = (x + last_ans - 1) % n + 1; }; auto find_block_id = [&](int id) -> pair<int, int> { int res = 0; for (int i = 0; i < block_num; i++) { if (res + (int) block[i].size() >= id) { return {i, res}; } res += block[i].size(); } }; auto remove = [&](vector<int>& v, vector<int>& c, int id) { auto it = v.begin() + id; int ret = *it; c[ret]--; v.erase(it); return ret; }; auto insert = [&](vector<int>& v, vector<int>& c, int id, int val) { auto it = v.begin() + id; c[val]++; v.insert(it, val); }; auto rebuild = [&]() { vector<vector<int>> new_block(block_num); int id = 0; for (int i = 0; i < block_num; i++) { for (auto it : block[i]) { int block_id = id / block_size; new_block[block_id].push_back(it); cnt[i][it]--; cnt[block_id][it]++; id++; } } block = new_block; }; for (int p = 1; p <= q; p++) { if (p % block_size == 0) rebuild(); int t, l, r; cin >> t >> l >> r; decode(l), decode(r); if (l > r) swap(l, r); auto [l_bid, l_pre] = find_block_id(l); auto [r_bid, r_pre] = find_block_id(r); int l_id = l - l_pre - 1, r_id = r - r_pre - 1; if (t == 1) { if (l == r) continue; auto val = remove(block[r_bid], cnt[r_bid], r_id); insert(block[l_bid], cnt[l_bid], l_id, val); } else { int k; cin >> k; decode(k); int ans = 0; if (l_bid == r_bid) { for (int i = l_id; i <= r_id; i++) ans += (block[l_bid][i] == k); } else { for (int i = l_bid + 1; i <= r_bid - 1; i++) ans += cnt[i][k]; for (int i = l_id; i < (int) block[l_bid].size(); i++) ans += (block[l_bid][i] == k); for (int i = 0; i <= r_id; i++) ans += (block[r_bid][i] == k); } cout << ans << '\n'; last_ans = ans; } } return 0;
} ä¸Šè¿°è¬›çš„ä½œæ³•æ˜¯å®˜æ–¹çš„è§£ç­”ï¼ŒåŒæ™‚é‹ç”¨äº†å°åºåˆ—åˆ†å¡Šèˆ‡æ“ä½œåˆ†å¡Šçš„ç²¾ç¥ã€‚é‚£æœ‰æ²’æœ‰å…¶ä»–çš„æ–¹æ³•å‘¢ï¼Ÿ æœƒéœ€è¦å°æ“ä½œåˆ†å¡Šçš„åŸå› æ˜¯åœ¨æš´åŠ›æ’å…¥èˆ‡åˆªé™¤å…ƒç´ æ™‚ï¼Œæ¯ä¸€å¡Šçš„å¤§å°æœƒè¢«æ”¹è®Šã€‚ä¹Ÿå› ç‚ºæ¯ä¸€å¡Šå¤§å°æœƒè¢«æ”¹è®Šï¼Œå°è‡´æˆ‘å€‘æœƒéœ€è¦å¦‚ä¸Šç¨‹å¼ç¢¼çš„ find_block_id å‡½æ•¸ä¾†æ‰¾ç¬¬ i å€‹å…ƒç´ åœ¨å“ªä¸€å¡Šã€‚ è¦ç¶­æŒæ¯ä¸€å¡Šçš„å¤§å°æ˜¯ \( O\sqrt{N} \)ï¼Œæˆ‘å€‘å¯ä»¥åœ¨æ¯æ¬¡æ“ä½œä¸€çµæŸå¾Œï¼Œä¸»å‹•èª¿æ•´ä¾†ç¶­æŒã€‚å‡è¨­æˆ‘å€‘å°‡æ©˜è‰²çš„å…ƒç´ å¾ç¬¬å››å¡Šç§»å‹•åˆ°ç¬¬ä¸€å¡Šä¸Šï¼Œå¦‚ä¸‹åœ–ã€‚ æ­¤æ™‚ç¬¬ä¸€å¡Šçš„å…ƒç´ æœƒå¤šä¸€å€‹è€Œç¬¬å››å¡Šçš„å…ƒç´ æœƒå°‘ä¸€å€‹ã€‚æˆ‘å€‘åªè¦å¾ç¬¬ä¸€å¡Šé–‹å§‹å°‡æœ€å³é‚Šå¤šçš„é‚£ä¸€å¡Šä¸Ÿçµ¦ä¸‹ä¸€å¡Šï¼Œä¸‹ä¸€å¡Šå°‡ä»–æœ€å³é‚Šå¤šçš„é‚£ä¸€å¡Šçµ¦ä¸‹ä¸€å¡Šï¼Œç›´åˆ°å°‘ä¸€å€‹çš„å¡Šè¢«è£œè¶³ç‚ºæ­¢ã€‚ ä¹Ÿå°±æ˜¯èªªï¼Œç¬¬ä¸€ç¨®æ“ä½œæš´åŠ›æ’å…¥åˆªé™¤å…ƒç´ å¾Œï¼Œå¾ \( l \) æ‰€åœ¨çš„å¡Šé–‹å§‹ï¼ŒæŒçºŒå°‡æœ€å¾Œé¢çš„å…ƒç´ æ¨åˆ°ä¸‹ä¸€å¡Šçš„é–‹é ­ï¼Œç›´åˆ° \( r \) æ‰€åœ¨çš„å¡Šç‚ºæ­¢ã€‚å› ç‚ºè‡³å¤š \( O(\sqrt{N}) \) å¡Šï¼Œæ­é…ä½¿ç”¨èƒ½ \( O(1) \) åœ¨æœ€å‰é¢èˆ‡æœ€å¾Œé¢æ’å…¥/åˆªé™¤çš„ dequeï¼Œå‰‡é€™æ­¥çš„æ™‚é–“è¤‡é›œåº¦ç‚º \( O(\sqrt{N}) \)ã€‚é€™æ¨£èƒ½ä¿è­‰ç¬¬ä¸€ç¨®æ“ä½œä¹‹å¾Œé‚„æ˜¯æ¯ \( O(\sqrt{N}) \) å€‹å…ƒç´ åœ¨åŒä¸€å¡Šã€‚ç¸½æ™‚é–“è¤‡é›œåº¦ä¸è®Šã€‚ é€™æ¨£åšçš„å¥½è™•æ˜¯æ¯ä¸€æ¬¡æ“ä½œçµæŸå¾Œç¬¬ i å€‹å…ƒç´ ä»ç„¶æœƒåœ¨ç¬¬ i / block_size å¡Šä¸Šã€‚å°±ä¸ç”¨å†é¡å¤–æ‰¾ç¬¬ i å¡Šåœ¨å“ªã€‚ä¹Ÿä¸éœ€è¦é‡å»ºåºåˆ—åˆ†å¡Šã€‚ Solution Code ä½¿ç”¨ 0-based indexï¼Œé€™æ¨£æ‰èƒ½æ–¹ä¾¿é€é i % block_size å–å¾—ç¬¬ i å€‹å…ƒç´ åœ¨ç¬¬ i / block_size çš„å“ªå€‹ä½ç½®ã€‚ #include <bits/stdc++.h>
using namespace std; int main() { cin.tie(0); ios_base::sync_with_stdio(0); int block_size = 320; int block_num = 320; int n; cin >> n; vector<int> a(n + 1); vector<deque<int>> block(block_num); vector<vector<int>> cnt(block_num, vector<int>(n + 1)); for (int i = 0; i < n; i++) { cin >> a[i]; int block_id = i / block_size; block[block_id].push_back(a[i]); cnt[block_id][a[i]]++; } int q; cin >> q; int last_ans = 0; auto decode = [&](int& x) { x = (x + last_ans - 1) % n + 1; }; auto remove = [&](deque<int>& deq, vector<int>& c, int id) { auto it = deq.begin() + id; int ret = *it; c[ret]--; deq.erase(it); return ret; }; auto insert = [&](deque<int>& deq, vector<int>& c, int id, int val) { auto it = deq.begin() + id; c[val]++; deq.insert(it, val); }; auto balance = [&](int l, int r) { for (int i = l; i < r; i++) { int val = block[i].back(); cnt[i + 1][val]++; block[i + 1].push_front(block[i].back()); cnt[i][val]--; block[i].pop_back(); } }; auto calc = [&](int l, int r, int k) { int ans = 0; while (l <= r) { if (l % block_size == 0 && l + block_size - 1 <= r) { ans += cnt[l / block_size][k]; l += block_size; } else { ans += (block[l / block_size][l % block_size] == k); l++; } } return ans; }; while (q--) { int t, l, r; cin >> t >> l >> r; decode(l), decode(r); if (l > r) swap(l, r); l--, r--; int l_bid = l / block_size, l_id = l % block_size; int r_bid = r / block_size, r_id = r % block_size; if (t == 1) { if (l == r) continue; auto val = remove(block[r_bid], cnt[r_bid], r_id); insert(block[l_bid], cnt[l_bid], l_id, val); balance(l_bid, r_bid); } else { int k; cin >> k; decode(k); last_ans = calc(l, r, k); cout << last_ans << '\n'; } } return 0;
} ç­†è€…è‡ªå·±æ¯”è¼ƒå–œæ­¡ç¬¬ä¸€ç¨®è§£æ³•çš„æƒ³æ³•ï¼Œä½†æœƒé¸æ“‡ç¬¬äºŒç¨®è§£æ³•ä¾†å¯¦ä½œã€‚

