SPOJ - Polynomial Multiplication çµ¦å®šå…©å€‹ \( n \) æ¬¡å¤šé …å¼çš„ä¿‚æ•¸ï¼Œè¼¸å‡ºå…©å€‹å¤šé …å¼ç›¸ä¹˜çš„å¤šé …å¼ä¿‚æ•¸ã€‚ Solution è®€å–è¼¸å…¥è³‡æ–™å¾Œï¼Œç›´æ¥å‘¼å« multipy() å³å¯ã€‚ Solution Code #include <iostream>
#include <cmath>
#include <complex>
#include <vector> using namespace std;
using cd = complex<double>;
const double PI = acos(-1); void fft(vector<cd> & a, bool invert) { int n = a.size(); if (n == 1) return; vector<cd> a0(n / 2), a1(n / 2); for (int i = 0; 2 * i < n; i++) { a0[i] = a[2*i]; a1[i] = a[2*i+1]; } fft(a0, invert); fft(a1, invert); double ang = 2 * PI / n * (invert ? -1 : 1); cd w(1), wn(cos(ang), sin(ang)); for (int i = 0; 2 * i < n; i++) { a[i] = a0[i] + w * a1[i]; a[i + n/2] = a0[i] - w * a1[i]; if (invert) { a[i] /= 2; a[i + n/2] /= 2; } w *= wn; }
} vector<long long> multiply(vector<long long> const& a, vector<long long> const& b) { vector<cd> fa(a.begin(), a.end()), fb(b.begin(), b.end()); long long n = 1; while (n < a.size() + b.size()) n <<= 1; fa.resize(n); fb.resize(n); fft(fa, false); fft(fb, false); for (long long i = 0; i < n; i++) fa[i] *= fb[i]; fft(fa, true); vector<long long> result(n); for (long long i = 0; i < n; i++) result[i] = round(fa[i].real()); return result;
} void solve() { long long n; cin >> n; vector<long long> a(n+1), b(n+1); for (long long i = 0; i <= n; ++i) cin >> a[i]; for (long long i = 0; i <= n; ++i) cin >> b[i]; vector<long long> result = multiply(a, b); for (long long i = 0; i <= (n << 1); ++i) cout << result[i] << " "; cout << "\n"; return;
} int main() { long long T; cin >> T; while(T--) solve(); return 0;
}

