} int main() { cin >> s; slen = s.length(); vector<long long> total(slen, 0), p(slen), r(slen), tmp; for (int i = 0; i < slen; ++i) p[i] = r[slen - 1 - i] = (s[i] == 'a' ? 1LL : 0LL); tmp = multiply(p, r); for (int i = 0; i < slen; ++i) total[i] += tmp[i]; for (int i = 0; i < slen; ++i) p[i] = r[slen - 1 - i] = (s[i] == 'b' ? 1LL : 0LL); tmp = multiply(p, r); for (int i = 0; i < slen; ++i) total[i] += tmp[i]; for (int i = 0; i < slen; ++i) p[i] = r[slen - 1 - i] = (s[i] == 'c' ? 1LL : 0LL); tmp = multiply(p, r); for (int i = 0; i < slen; ++i) total[i] += tmp[i]; int ans = -1; for (int i = 0; i < slen-1; ++i) if (total[i] > ans) ans = total[i]; cout << ans << "\n"; for (int i = slen - 1; i >= 0; --i) if (total[i] == ans) cout << slen - 1 - i << " "; if (ans == 0) cout << slen << " "; cout << "\n"; return 0;
}

