const ll INF = (1LL<<60); ll n, x, y; ll ask(vector<ll>& rset) { if (SZ(rset) == 0) return 0; cout << "? " << SZ(rset) << " "; for (ll i = 0;i < SZ(rset); ++i) cout << rset[i]+1 << " \n"[i==SZ(rset)-1]; ll ret = 0; cin >> ret; return (ret == (x^y) || ret == y);
} void solve() { ll m = 0, l = 0, ans = 0, diff = 0; cin >> n >> x >> y; for (ll level = 0; (1<<level) < n; ++level) { vector<ll> rset; for (ll i = 0;i < n; ++i) if ((i>>level)&1) rset.pb(i); diff |= (ask(rset) << level); if ((diff>>level)&1) l = level; m = level; } for (ll level = m; level >= 0; --level) { vector<ll> rset; if (level > l) { for (ll i = 0;i < n; ++i) if (((i>>level)&1) && ((i>>l)&1)) rset.pb(i); if (ask(rset)) { ans |= (1<<level); } } else if (level < l) { for (ll i = 0;i < n; ++i) if ((i>>(level+1)) == (ans>>(level+1)) && ((i>>level)&1)) rset.pb(i); if (ask(rset)) { ans |= (1<<level); } } else { ans |= (1<<level); } } ll a = ans+1, b = (ans^diff)+1; if (a > b) swap(a, b); cout << "! " << a << " " << b << "\n";
