} void solve() { ll n; cin >> n; vector<ll> ans(n), u(n); if ((n&1)) { ll sum = 0, os = 0; for (ll i = 0;i < n; ++i) { u[i] = get(i, (i+1)%n); sum += u[i]; if (i&1) os += u[i]; } sum >>= 1; ans[0] = sum - os; for (ll i = 0;i < n-1; ++i) ans[i+1] = u[i]-ans[i]; } else { ll sum = 0, os = 0; for (ll i = 0;i < n; i += 2) { u[i] = get(i, (i+1)%n); sum += u[i]; } for (ll i = 1;i < n-1; i += 2) { u[i] = get(i, (i+1)%n); os += u[i]; } u[n-1] = sum - os; ans[0] = (get(0, n-2)-u[n-2]+u[n-1])/2; for (ll i = 0;i < n-1; ++i) ans[i+1] = u[i]-ans[i]; } cout << "! " << ans << "\n";
