using namespace std; using ll = long long;
using pll = pair<ll, ll>;
const ll N = 200005; int s[N], f[N];
ll dp[N]; ll cal(ll x, pll line){ return x * line.first + line.second;
} bool cmp(pll line1, pll line2, pll line3){ ll a = line3.second - line1.second; ll b = line2.second - line1.second; ll c = line1.first - line3.first; ll d = line1.first - line2.first; return a * d <= b * c;
} int main(){ int n; cin >> n >> f[0]; for(int i = 1; i <= n; i++){ cin >> s[i]; } for(int i = 1; i <= n; i++){ cin >> f[i]; } deque <pll> dq; dq.emplace_back(f[0], 0); for(int i = 1; i <= n; i++){ while(dq.size() >= 2 && cal(s[i], dq[0]) > cal(s[i], dq[1])){ dq.pop_front(); } dp[i] = cal(s[i], dq[0]); pll line(f[i], dp[i]); int sz = dq.size(); while(sz >= 2 && cmp(dq[sz - 2], dq[sz - 1], line)){ dq.pop_back(); sz--; } dq.emplace_back(line); } cout << dp[n] << "\n"; return 0;
} æˆ‘å€‘ä¾†çœ‹ä¸€å€‹ç¨å¾®è¤‡é›œçš„ä¾‹é¡Œã€‚ TIOJ 1676 - çƒé¾œç–Šç–Šæ¨‚ çµ¦å®šä¸€å€‹é•·åº¦ç‚º \(n\) çš„é™£åˆ—èˆ‡ä¸€å€‹æ•´æ•¸ \(k\)ï¼Œä½ å¯ä»¥å°‡é™£åˆ—åˆ‡æˆæ•¸æ®µé€£çºŒçš„å€é–“ï¼Œå€é–“å¤§å°ä¸èƒ½è¶…é \(k\)ï¼Œæ±‚åˆ‡å®Œä¹‹å¾Œçš„æœ€å¤§åƒ¹å€¼ã€‚> ä¸€æ®µé•·åº¦ç‚º \(x\)ã€ç‚ºæ•´å€‹é™£åˆ—ç”±å·¦è‡³å³æ•¸ä¾†ç¬¬ \(m\) å€‹å€é–“ï¼Œå…¶åƒ¹å€¼è¢«å®šç¾©ç‚º \((m-1)\times \sum\limits^r_{i=l}a_i-x^2\)ã€‚å…¶ä¸­ \(l\)ã€\(r\) åˆ†åˆ¥ç‚ºå€é–“å·¦å³ç•Œã€\(a_i\) ç‚ºé™£åˆ—ä¸­ç¬¬ \(i\) å€‹å€¼ã€‚ \(1 \leq k \leq n \leq 500000\) ä¸€æ¨£ï¼Œæˆ‘å€‘å…ˆè©¦è‘—åˆ—å‡ºè½‰ç§»å¼ã€‚ä»¤ \(f(i)\) ç‚ºå‰ \(i\) å€‹æ•¸å­—è²¢ç»çš„åƒ¹å€¼ï¼ˆåŒæ™‚ç¬¬ \(i\) å€‹å…ƒç´ ç‚ºå€é–“å³ç•Œï¼‰ã€‚ è½‰ç§»å¼ç‚ºï¼š\(f(i)=\underset{j<i}{\max}(f(j)-(i-j)^2+suf(i+1))\) å…¶ä¸­ \(suf(i+1)\) æ˜¯å¾Œç¶´å’Œï¼Œç¯„åœå¾ \(i+1\) åˆ° \(n\)ã€‚ é™¤äº† \(suf(i+1)\) é€™ä¸€é …å¤–ï¼Œå…¶ä»–éƒ¨åˆ†é‚„è »ç›´è§€çš„ã€‚è€Œ \(suf(i+1)\) é€™ä¸€é …å°æ‡‰åˆ°é¡Œç›®çš„ \((m-1)\times \sum\limits^r_{i=l}a_i\)ï¼Œæˆ‘å€‘çœ‹ä¸€å¼µç¤ºæ„åœ–ä»¥æ›´å¥½ç†è§£ \(suf(i+1)\) çš„æ„ç¾©ï¼š ä¸Šé¢çš„ç¤ºæ„åœ–å°æ¯å€‹å€é–“ç•«å‡ºäº†å…¶ \((m-1)\times \sum\limits^r_{i=l}a_i\) çš„å€¼ã€‚æˆ‘å€‘å¯ä»¥å°‡å®ƒå€‘é‡æ–°åˆ†å‰²è®Šæˆä¸‹é¢é€™æ¨£ï¼š å¯ä»¥ç™¼ç¾é€™æ¯ä¸€å¡Šå…¶å¯¦å°±æ˜¯ä¸€æ®µå¾Œç¶´ï¼Œè€Œä¸”æ¯å€‹å¾Œç¶´çš„èµ·é»å°æ‡‰åˆ°æ¯å€‹å€é–“çš„èµ·é»ï¼ˆé™¤äº†ç¬¬ä¸€å€‹å€é–“ä¸è€ƒæ…®ï¼‰ã€‚æˆ‘å€‘è½‰ç§»å¼ä¸­çš„ \(i\) æ˜¯å€é–“çµ‚é»ï¼Œå› æ­¤ \(i+1\) ä¾¿å°æ‡‰åˆ°å€é–“èµ·é»ã€‚è€Œæ‰€æœ‰çš„ \(suf(i+1)\) åŠ èµ·ä¾†ï¼Œå°±å°æ‡‰åˆ°æ‰€æœ‰å€é–“çš„ \((m-1)\times \sum\limits^r_{i=l}a_i\)ï¼Œé€™å€‹éƒ¨åˆ†çš„ç¸½å’Œã€‚ ç­è§£è½‰ç§»å¼çš„æ¨å°å¾Œï¼Œæˆ‘å€‘å›ä¾†è§€å¯Ÿé€™å€‹è½‰ç§»å¼ã€‚å®ƒçœ‹èµ·ä¾†èˆ‡ \(y=ax+b\) çš„å½¢å¼ä¸å¤ªåƒï¼Œä½†æˆ‘å€‘è©¦è‘—å°‡å®ƒå±•é–‹ï¼š \(f(i)=\underset{i>j>i-k}{\max}(f(j)-(i-j)^2+suf(i+1))\) \(=\underset{i>j>i-k}{\max}(f(j)-i^2+2ij-j^2+suf(i+1))\) \(=\underset{i>j>i-k}{\max}(f(j)-j^2+2ij)-i^2+suf(i+1)\) å¯ä»¥ç™¼ç¾ \(max\) è£¡é¢çš„éƒ¨åˆ†è®Šæˆ \(y=ax+b\) çš„å½¢å¼äº†ï¼š\[y=f(i)\] \[x=2i\] \[a=j\] \[b=f(j)-j^2\] é€™éº¼ä¸€ä¾†å°±å¯ä»¥ä½¿ç”¨å‰›å‰›æåˆ°çš„æŠ€å·§äº†ï¼ä½†ä»”ç´°è§€å¯Ÿä¸€ä¸‹ï¼Œé€™å€‹è½‰ç§»å¼èˆ‡ä¸Šä¸€å€‹ä¾‹é¡Œæœ‰å…©å€‹ä¸åŒ