using namespace std; using ll = long long;
using pll = pair<ll, ll>;
const ll N = 300005; ll pret[N], prec[N];
ll dp[N];
pll v[N];
ll sz; inline ll cal(ll x, pll line){ return x * line.first + line.second;
}
inline bool cmp2(pll p1, pll p2, ll x){ return (p2.second - p1.second) > (p1.first - p2.first) * x;
}
inline pll find_line(ll x){ int l = 0, r = sz - 2, mid, ans = r + 1; while(l <= r){ mid = (l + r) >> 1; if(cmp2(v[mid], v[mid + 1], x)){ r = mid - 1; ans = mid; } else l = mid + 1; } return v[ans];
} inline bool cmp(pll line1, pll line2, pll line3){ __int128 a = line3.second - line1.second; __int128 b = line2.second - line1.second; __int128 c = line1.first - line3.first; __int128 d = line1.first - line2.first; return a * d <= b * c;
} int main(){ ios_base::sync_with_stdio(0); cin.tie(0); ll n, s; cin >> n >> s; for(int i = 1; i <= n; i++){ cin >> pret[i] >> prec[i]; pret[i] += pret[i - 1]; prec[i] += prec[i - 1]; } v[sz++] = pll(0, 0); for(int i = 1; i <= n; i++){ pll fl = find_line(pret[i]); dp[i] = cal(pret[i], fl) + prec[i] * pret[i] + s * prec[n]; pll line(-prec[i], dp[i] - s * prec[i]); while(sz >= 2 && cmp(v[sz - 2], v[sz - 1], line)) sz--; v[sz++] = line; } cout << dp[n] << "\n"; return 0;
}

