using namespace std; struct LCA { int time = 0; vector<int> par, tin, tout; vector<vector<int>> adj; void init(int n) { par.assign(n + 1, 0); tin.assign(n + 1, 0); tout.assign(n + 1, 0); adj.assign(n + 1, vector<int>()); } void add_edge(int u, int v) { adj[u].emplace_back(v); adj[v].emplace_back(u); } void dfs(int u = 1, int p = 0) { tin[u] = ++time; par[u] = p; for (int v : adj[u]) if (v != p) dfs(v, u); tout[u] = ++time; } void build() { dfs(); tout[0] = ++time; } bool is_ancestor(int u, int v) { return tin[u] <= tin[v] && tout[v] <= tout[u]; } int query(int u, int v) { while (!is_ancestor(u, v)) u = par[u]; return u; }
}; int main() { ios::sync_with_stdio(0); cin.tie(0); int N, Q; cin >> N >> Q; LCA lca; lca.init(N); for (int i = 2; i <= N; ++i) { int p; cin >> p; lca.add_edge(i, p); } lca.build(); for (int i = 0; i < Q; ++i) { int u, v; cin >> u >> v; cout << lca.query(u, v) << "\n"; } return 0;
} ç›´è§€çš„åšæ³•é€šå¸¸å¾ˆæ…¢ï¼Œå¯ä»¥è§€å¯Ÿåˆ°åœ¨ä¸Šè¿°åšæ³•ç•¶ä¸­ï¼Œæˆ‘å€‘æ˜¯ä¸€å€‹é»ä¸€å€‹é»æ…¢æ…¢å¾€ä¸Šæª¢æŸ¥ï¼Œå°è‡´å–®æ¬¡æŸ¥è©¢çš„æ™‚é–“è¤‡é›œåº¦ç‚ºç·šæ€§ã€‚è€Œæˆ‘å€‘æ¥ä¸‹ä¾†è¦ä»‹ç´¹å¦å¤–ä¸€ç¨®åšæ³• Binary Liftingï¼Œä¸­æ–‡ç¨±ä½œå€å¢æ³•ï¼Œå³æ˜¯é‡å°é€™ä¸€é»å»åšå„ªåŒ–ã€‚

